import React, { useState, useEffect } from 'react';

const Order = () => {
  const [formData, setFormData] = useState({
    customer_name: '',
    customer_contact: '',
    coffee: '',
    size: 'Small',
    quantity: 1
  });
  const [cart, setCart] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [successMessage, setSuccessMessage] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showReceipt, setShowReceipt] = useState(false);

  const priceMap = {
    "Barako Coffee": 80,
    "Cafe Latte": 120,
    "Cappuccino": 110,
    "Mocha": 130,
    "Cold Brew": 140,
    "Caffe Americano": 100
  };

  const sizeMultiplier = {
    "Small": 1,
    "Medium": 1.25,
    "Large": 1.5
  };

  const calculateTotal = () => {
    const basePrice = priceMap[formData.coffee] || 0;
    const multiplier = sizeMultiplier[formData.size] || 1;
    const total = basePrice * multiplier * formData.quantity;
    setTotalPrice(total);
    return total;
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (formData.customer_name.length < 2) {
      newErrors.customer_name = "Please enter your full name (at least 2 characters).";
    }
    
    const contactRegex = /^[0-9]{11}$/;
    if (!contactRegex.test(formData.customer_contact)) {
      newErrors.customer_contact = "Please enter a valid 11-digit contact number.";
    }
    
    if (!formData.coffee) {
      newErrors.coffee = "Please select a coffee.";
    }
    
    if (formData.quantity < 1) {
      newErrors.quantity = "Quantity must be at least 1.";
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'quantity' ? parseInt(value) || 1 : value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    setSuccessMessage(`Thank you for your order! We'll contact you soon at ${formData.customer_contact}.`);
    
    // Reset form
    setFormData({
      customer_name: '',
      customer_contact: '',
      coffee: '',
      size: 'Small',
      quantity: 1
    });
    setTotalPrice(0);
    setIsSubmitting(false);
  };

  // Calculate total whenever form data changes
  useEffect(() => {
    calculateTotal();
  }, [formData.coffee, formData.size, formData.quantity]);

  return (
    <>
      <section className="container mt-5 pt-5">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="text-center mb-5">
              <h2 className="display-4 fw-bold mb-3">Place Your Order</h2>
              <p className="lead">Fill out the form below to place your coffee order</p>
            </div>
            
            {successMessage && (
              <div className="alert alert-success text-center" role="alert">
                <i className="fas fa-check-circle me-2"></i>
                {successMessage}
              </div>
            )}
            
            <div className="card">
              <div className="card-body p-4">
                <form onSubmit={handleSubmit}>
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label htmlFor="customerName" className="form-label fw-bold">
                        <i className="fas fa-user me-2"></i>Full Name
                      </label>
                      <input 
                        type="text" 
                        id="customerName" 
                        name="customer_name" 
                        className={`form-control ${errors.customer_name ? 'is-invalid' : ''}`}
                        value={formData.customer_name}
                        onChange={handleInputChange}
                        placeholder="Enter your full name"
                        required 
                      />
                      {errors.customer_name && (
                        <div className="invalid-feedback">
                          {errors.customer_name}
                        </div>
                      )}
                    </div>
                    
                    <div className="col-md-6 mb-3">
                      <label htmlFor="customerContact" className="form-label fw-bold">
                        <i className="fas fa-phone me-2"></i>Contact Number
                      </label>
                      <input
                        type="text"
                        id="customerContact"
                        name="customer_contact"
                        className={`form-control ${errors.customer_contact ? 'is-invalid' : ''}`}
                        value={formData.customer_contact}
                        onChange={handleInputChange}
                        placeholder="09XXXXXXXXX"
                        required
                      />
                      {errors.customer_contact && (
                        <div className="invalid-feedback">
                          {errors.customer_contact}
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label htmlFor="coffee" className="form-label fw-bold">
                        <i className="fas fa-coffee me-2"></i>Select Coffee
                      </label>
                      <select 
                        id="coffee" 
                        name="coffee" 
                        className={`form-select ${errors.coffee ? 'is-invalid' : ''}`}
                        value={formData.coffee}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="" disabled>-- Choose your coffee --</option>
                        <option value="Barako Coffee">Barako Coffee - ₱80</option>
                        <option value="Cafe Latte">Cafe Latte - ₱120</option>
                        <option value="Cappuccino">Cappuccino - ₱110</option>
                        <option value="Mocha">Mocha - ₱130</option>
                        <option value="Cold Brew">Cold Brew - ₱140</option>
                        <option value="Caffe Americano">Caffe Americano - ₱100</option>
                      </select>
                      {errors.coffee && (
                        <div className="invalid-feedback">
                          {errors.coffee}
                        </div>
                      )}
                    </div>
                    
                    <div className="col-md-6 mb-3">
                      <label htmlFor="size" className="form-label fw-bold">
                        <i className="fas fa-expand-arrows-alt me-2"></i>Size
                      </label>
                      <select 
                        id="size" 
                        name="size" 
                        className="form-select" 
                        value={formData.size}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="Small">Small (₱0)</option>
                        <option value="Medium">Medium (+25%)</option>
                        <option value="Large">Large (+50%)</option>
                      </select>
                    </div>
                  </div>
                  
                  <div className="row">
                    <div className="col-md-6 mb-3">
                      <label htmlFor="quantity" className="form-label fw-bold">
                        <i className="fas fa-sort-numeric-up me-2"></i>Quantity
                      </label>
                      <input
                        type="number"
                        id="quantity"
                        name="quantity"
                        className={`form-control ${errors.quantity ? 'is-invalid' : ''}`}
                        value={formData.quantity}
                        onChange={handleInputChange}
                        min="1"
                        max="10"
                        required
                      />
                      {errors.quantity && (
                        <div className="invalid-feedback">
                          {errors.quantity}
                        </div>
                      )}
                    </div>
                    
                    <div className="col-md-6 mb-3">
                      <label className="form-label fw-bold">
                        <i className="fas fa-calculator me-2"></i>Total Amount
                      </label>
                      <div className="card bg-light">
                        <div className="card-body text-center">
                          <h4 className="fw-bold text-warning mb-0">₱{totalPrice.toFixed(2)}</h4>
                          <small className="text-muted">
                            {formData.coffee && (
                              <>
                                {formData.coffee} × {formData.quantity} ({formData.size})
                              </>
                            )}
                          </small>
                        </div>
                      </div>
                    </div>
                  </div>
                  
                  <div className="d-grid gap-2 d-md-flex justify-content-md-end">
                    <button 
                      type="submit" 
                      className="btn btn-warning btn-lg"
                      disabled={isSubmitting}
                    >
                      {isSubmitting ? (
                        <>
                          <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                          Processing...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-shopping-cart me-2"></i>Place Order
                        </>
                      )}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>

      <footer className="text-center py-4">
        <div className="container">
          <div className="row">
            <div className="col-md-6">
              <h5 className="fw-bold mb-3">Kapeko Coffee</h5>
              <p className="text-muted">Thank you for choosing us for your coffee needs.</p>
            </div>
            <div className="col-md-6">
              <h6 className="fw-bold mb-3">Quick Links</h6>
              <div className="d-flex justify-content-center gap-3 flex-wrap">
                <a href="/menu" className="text-warning text-decoration-none">Menu</a>
                <a href="/contact" className="text-warning text-decoration-none">Contact</a>
                <a href="/feedback" className="text-warning text-decoration-none">Feedback</a>
              </div>
            </div>
          </div>
          <hr className="my-4" />
          <p className="mb-0">© 2025 Kapeko Coffee | Thank you for ordering with us ☕️</p>
        </div>
      </footer>
    </>
  );
};

export default Order;
