import React, { useState } from 'react';

const Feedback = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    rating: '',
    message: ''
  });
  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Simulate form submission
    setSuccessMessage("Thank you for your feedback! We appreciate your time and support.");
    
    // Reset form
    setFormData({
      name: '',
      email: '',
      rating: '',
      message: ''
    });
  };

  return (
    <>
      <main className="container mt-5 pt-5">
        <h2 className="text-center mb-4">We Value Your Feedback</h2>
        {successMessage && (
          <div className="alert alert-success text-center" role="alert">
            {successMessage}
          </div>
        )}
        <form onSubmit={handleSubmit} className="mx-auto mb-5" style={{maxWidth: '500px'}}>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">Your Name</label>
            <input 
              type="text" 
              id="name" 
              name="name" 
              className="form-control" 
              placeholder="Enter your name" 
              value={formData.name}
              onChange={handleInputChange}
              required 
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">Email Address</label>
            <input 
              type="email" 
              id="email" 
              name="email" 
              className="form-control" 
              placeholder="Enter your email" 
              value={formData.email}
              onChange={handleInputChange}
              required 
            />
          </div>
          <div className="mb-3">
            <label htmlFor="rating" className="form-label">Rate Your Experience</label>
            <select 
              id="rating" 
              name="rating" 
              className="form-select" 
              value={formData.rating}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>Select rating</option>
              <option value="5">üåüüåüüåüüåüüåü - Excellent</option>
              <option value="4">üåüüåüüåüüåü - Very Good</option>
              <option value="3">üåüüåüüåü - Good</option>
              <option value="2">üåüüåü - Fair</option>
              <option value="1">üåü - Poor</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="message" className="form-label">Your Feedback</label>
            <textarea 
              id="message" 
              name="message" 
              rows="4" 
              className="form-control" 
              placeholder="Write your comments here..." 
              value={formData.message}
              onChange={handleInputChange}
              required
            ></textarea>
          </div>
          <button type="submit" className="btn btn-warning w-100">Submit Feedback</button>
        </form>
      </main>

      <footer className="text-center py-4 bg-light">
        <p className="mb-0">¬© 2025 Kapeko Coffee | Stay connected, stay caffeinated ‚ö°Ô∏è</p>
      </footer>
    </>
  );
};

export default Feedback;
