import React, { useState } from 'react';

const Order = () => {
  const [formData, setFormData] = useState({
    customer_name: '',
    customer_contact: '',
    coffee: '',
    size: 'Small',
    quantity: 1
  });
  const [totalPrice, setTotalPrice] = useState(0);
  const [successMessage, setSuccessMessage] = useState('');

  const priceMap = {
    "Barako Coffee": 80,
    "Cafe Latte": 120,
    "Cappuccino": 110,
    "Mocha": 130,
    "Cold Brew": 140,
    "Caffe Americano": 100
  };

  const sizeMultiplier = {
    "Small": 1,
    "Medium": 1.25,
    "Large": 1.5
  };

  const calculateTotal = () => {
    const basePrice = priceMap[formData.coffee] || 0;
    const multiplier = sizeMultiplier[formData.size] || 1;
    const total = basePrice * multiplier * formData.quantity;
    setTotalPrice(total);
    return total;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'quantity' ? parseInt(value) || 1 : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Validation
    if (formData.customer_name.length < 2) {
      alert("Please enter your full name (at least 2 characters).");
      return;
    }
    
    const contactRegex = /^[0-9]{11}$/;
    if (!contactRegex.test(formData.customer_contact)) {
      alert("Please enter a valid 11-digit contact number.");
      return;
    }
    
    if (!formData.coffee) {
      alert("Please select a coffee.");
      return;
    }

    // Simulate order submission
    setSuccessMessage(`Thank you for your order! We'll contact you soon at ${formData.customer_contact}.`);
    
    // Reset form
    setFormData({
      customer_name: '',
      customer_contact: '',
      coffee: '',
      size: 'Small',
      quantity: 1
    });
    setTotalPrice(0);
  };

  // Calculate total whenever form data changes
  React.useEffect(() => {
    calculateTotal();
  }, [formData.coffee, formData.size, formData.quantity]);

  return (
    <>
      <section className="container mt-5 pt-5">
        <h2 className="text-center mb-4">Place Your Order</h2>
        {successMessage && (
          <div className="alert alert-success text-center" role="alert">
            {successMessage}
          </div>
        )}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="customerName">Full Name</label>
            <input 
              type="text" 
              id="customerName" 
              name="customer_name" 
              className="form-control" 
              value={formData.customer_name}
              onChange={handleInputChange}
              required 
            />
          </div>
          <div className="mb-3">
            <label htmlFor="customerContact">Contact Number</label>
            <input
              type="text"
              id="customerContact"
              name="customer_contact"
              className="form-control"
              value={formData.customer_contact}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="coffee">Select Coffee</label>
            <select 
              id="coffee" 
              name="coffee" 
              className="form-select" 
              value={formData.coffee}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>-- Choose --</option>
              <option value="Barako Coffee">Barako Coffee - ₱80</option>
              <option value="Cafe Latte">Cafe Latte - ₱120</option>
              <option value="Cappuccino">Cappuccino - ₱110</option>
              <option value="Mocha">Mocha - ₱130</option>
              <option value="Cold Brew">Cold Brew - ₱140</option>
              <option value="Caffe Americano">Caffe Americano - ₱100</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="size">Size</label>
            <select 
              id="size" 
              name="size" 
              className="form-select" 
              value={formData.size}
              onChange={handleInputChange}
              required
            >
              <option value="Small">Small</option>
              <option value="Medium">Medium (+25%)</option>
              <option value="Large">Large (+50%)</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="quantity">Quantity</label>
            <input
              type="number"
              id="quantity"
              name="quantity"
              className="form-control"
              value={formData.quantity}
              onChange={handleInputChange}
              min="1"
              required
            />
          </div>
          <div className="mb-3">
            <h5>Total: <span>₱{totalPrice.toFixed(2)}</span></h5>
          </div>
          <button type="submit" className="btn btn-warning">Place Order</button>
        </form>
      </section>

      <footer className="text-center py-3">
        <p>© 2025 Kapeko Coffee | Thank you for ordering with us ☕️</p>
      </footer>
    </>
  );
};

export default Order;
